import React from 'react'
import './productslist.css'


   
const products = [
  {
    id: 1,
    name: "10 CM ELECTRIC SPARKLER 10 Pcs",
    content: "10 செ.மீ சாதா கம்பி",
    pack: "5 BOX",
    price: 50,
  },
  {
    id: 2,
    name: "10 CM COLOUR SPARKLER 10 Pcs",
    content: "10 செ.மீ color கம்பி",
    pack: "5 BOX",
    price: 55,
  },
  {
    id: 3,
    name: "12 CM ELECTRIC SPARKLER 10 Pcs",
    content: "12 செ.மீ சாதா கம்பி (5 BOX)",
    pack: "5 BOX",
    price: 85,
  },
  {
    id: 4,
    name: "12 CM COLOUR SPARKLER 10 Pcs",
    content: "12 செ.மீ color கம்பி (5 BOX)",
    pack: "5 BOX",
    price: 90,
  },

];
const productslist = () => {
  return (
    <div className="container">
      <h1 className="title">Crackers Price List</h1>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            {/* Left Content */}
            <div className="product-info">
              <p className="product-name">{product.id}. {product.name}</p>
              <p className="product-content">{product.content}</p>
              <p className="product-pack">{product.pack}</p>
              <p className="product-price">Rs. {product.price.toFixed(2)}</p>
            </div>

            {/* Right Controls */}
            <div className="controls">
              <button onClick={() => updateQty(product.id, -1, product.price)}>-</button>
              <input
                type="text"
                readOnly
                value={cart[product.id]?.qty || 0}
              />
              <button onClick={() => updateQty(product.id, 1, product.price)}>+</button>
              <p className="row-total">Rs.{cart[product.id]?.total || 0}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Net Total */}
      <div className="net-total">
        Net Total: ₹ {netTotal}
      </div>
    </div>
  
  )
}

export default productslist
