import React, { useState } from "react";
import './productslist.css'

const products = [
  {
    id: 1,
    name: "10 CM ELECTRIC SPARKLER 10 Pcs",
    content: "10 ‡Æö‡ØÜ.‡ÆÆ‡ØÄ ‡Æö‡Ææ‡Æ§‡Ææ ‡Æï‡ÆÆ‡Øç‡Æ™‡Æø",
    pack: "5 BOX",
    price: 50,
  },
  {
    id: 2,
    name: "10 CM COLOUR SPARKLER 10 Pcs",
    content: "10 ‡Æö‡ØÜ.‡ÆÆ‡ØÄ color ‡Æï‡ÆÆ‡Øç‡Æ™‡Æø",
    pack: "5 BOX",
    price: 55,
  },
  {
    id: 3,
    name: "12 CM ELECTRIC SPARKLER 10 Pcs",
    content: "12 ‡Æö‡ØÜ.‡ÆÆ‡ØÄ ‡Æö‡Ææ‡Æ§‡Ææ ‡Æï‡ÆÆ‡Øç‡Æ™‡Æø (5 BOX)",
    pack: "5 BOX",
    price: 85,
  },
  {
    id: 4,
    name: "12 CM COLOUR SPARKLER 10 Pcs",
    content: "12 ‡Æö‡ØÜ.‡ÆÆ‡ØÄ color ‡Æï‡ÆÆ‡Øç‡Æ™‡Æø (5 BOX)",
    pack: "5 BOX",
    price: 90,
  },

];

function CrackersList() {
  const [cart, setCart] = useState({});

  const updateQty = (id, change, price) => {
    setCart((prev) => {
      const qty = (prev[id]?.qty || 0) + change;
      if (qty < 0) return prev;
      return {
        ...prev,
        [id]: { qty, total: qty * price },
      };
    });
  };

  const netTotal = Object.values(cart).reduce((sum, item) => sum + item.total, 0);

  

  return (
    <div className="container">
      <h1 className="title">Crackers Price List</h1>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            {/* Left Content */}
            <div className="product-info">
              <p className="product-name">{product.id}. {product.name}</p>
              <p className="product-content">{product.content}</p>
              <p className="product-pack">{product.pack}</p>
              <p className="product-price">Rs. {product.price.toFixed(2)}</p>
            </div>

            {/* Right Controls */}
            <div className="controls">
              <button onClick={() => updateQty(product.id, -1, product.price)}>-</button>
              <input
                type="text"
                readOnly
                value={cart[product.id]?.qty || 0}
              />
              <button onClick={() => updateQty(product.id, 1, product.price)}>+</button>
              <p className="row-total">Rs.{cart[product.id]?.total || 0}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Net Total */}
      <div className="net-total">
        Net Total: ‚Çπ {netTotal}
      </div>
         <div className="print-btn">
        <button onClick={handlePrint}>üñ®Ô∏è Print / Save as PDF</button>
      </div>
    </div>
  );
}

export default CrackersList;
